import "../styles/CardEditor.css";
import React, { Component } from "react";
import TextareaAutosize from "react-textarea-autosize";
import EditButtons from "./EditButtons";

//import { Redirect } from "react-router-dom";

class CardEditor extends Component {

  state = {
    text: this.props.text || "",
    btn: ""
    //btn:JSON.parse(localStorage.getItem('allProjects')) || []

  };
  handleChangeText = event => this.setState({ text: event.target.value });

  onEnter = e => {
    const { text } = this.state;

    if (e.keyCode === 13) {
      e.preventDefault();
      this.props.onSave(text);
    }
  };
  /*componentDidMount(){
   const color = localStorage.getItem('buttonColor')
    if(color != undefined){
        this.setState({btn: color})
  }
}*/
//create the props function
/*constructor(props){
  super(props);
  this.state ={
    onColor:props.Event
  }
}*/

/*constructor() {
  super();
  this.onColor = this.onColor.bind(this);
}*/
//create color function
  color=(Event) => {
    //const onColor = Event.currentTarget.value;
   // Event.preventDefault();
   //this.props.filterUser(Event.target.value);
    this.props.onColor(Event.target.value);
    //onColor(this.setState);
    //this.onColor.bind(this)
    this.setState({btn:Event.target.value});
    //this.props.onColor(Event.target.value);

    //localStorage.setItem('buttonColor', Event.target.value);
  /*this.setState({
      btn: this.state.btn.concat(Event.target.value)
  },() => {
    localStorage.setItem('buttonColor', JSON.stringify(this.state.btn))
    })*/
  };
  constructor(props) {
    super(props);
    this.OnColor = this.OnColor.bind(this);
  }


  render() {
    const { text,btn } = this.state;
    const { onSave, onCancel, onDelete, adding} = this.props;


    return (
      <div className="Edit-Card">
        <div
        className = "Button"
        Button="onClick"
        >
        </div>

        <div className="Card">
          <TextareaAutosize
            autoFocus
            className="Edit-Card-Textarea"
            placeholder="Enter the text for this card..."
            value={text}
            onChange={this.handleChangeText}
            onKeyDown={this.onEnter}

          />

        </div>
        <EditButtons
          handleSave={() => onSave(text)}
          saveLabel={adding ? "Add card" : "Save"}
          handleDelete={onDelete}
          handleCancel={onCancel}
          handleColor={this.color}
          red={btn}
        />
      </div>

    );


  }
}

export default CardEditor;
