import "../styles/CardEditor.css";

import React, { Component } from "react";
import TextareaAutosize from "react-textarea-autosize";
import EditButtons from "./EditButtons";
var green = '#39D1B4';
var yellow = '#FFD712';

var Toggle = React.createClass({
	getInitialState: function(){
		return {
			color: green
		}
	},

	randomColor: function() {
	    var letters = '0123456789ABCDEF';
	    var colory = '#';
	    for (var i = 0; i < 6; i++ ) {
		  colory += letters[Math.floor(Math.random() * 16)];
	    }
	    this.setState({color: colory});

	},

	render: function(){
		return (
			<div style={{background: this.state.color}}>
				<button onClick={this.randomColor}>Make change</button>
			</div>
		);
	}
});

ReactDOM.render(<Toggle />, document.getElementById('app'));

class Colorful extends React.Component {
  constructor(...props) {
   super(...props);
   this.state = { color: "red" };
  }

  changeColor(color) { this.setState({ color }); }

  render() {
   return <div 
     style={{ color: this.state.color }}
     onClick={() => this.changeColor("blue")}
  >Click me!</div>;
 }
}

class CardEditor extends Component {

  state = {
    text: this.props.text || ""
  };
  handleChangeText = event => this.setState({ text: event.target.value });

  onEnter = e => {
    const { text } = this.state;

    if (e.keyCode === 13) {
      e.preventDefault();
      this.props.onSave(text);
    }
  };


  render() {
    const { text } = this.state;
    const { onSave, onCancel, onDelete, adding} = this.props;

    return (
      <div className="Edit-Card">
        <div
        className = "Button"
        Button="onClick"
        >
        </div>

        <div className="Card">
          <TextareaAutosize
            autoFocus
            className="Edit-Card-Textarea"
            placeholder="Enter the text for this card..."
            value={text}
            onChange={this.handleChangeText}
            onKeyDown={this.onEnter}
          />
        </div>
        <EditButtons
          handleSave={() => onSave(text)}
          saveLabel={adding ? "Add card" : "Save"}
          handleDelete={onDelete}
          handleCancel={onCancel}

        />

      </div>

    );


  }
}

export default CardEditor;
