import "../styles/EditButtons.css";
import React from "react";

var colors = ["red", "green", "yellow"],   // the color choices
    index = 0;                                     // index of the current color

document.getElementById("colorify").onclick = function() {
  document.body.style.background = colors[index];  // set the color of body to the current color
  index = (index + 1) % colors.length;             // increment index to point to the next color (if it goes beyond the length of the coices array get it back to 0 using the modulo %)
}
const EditButtons = ({ handleSave, saveLabel, handleDelete, handleCancel,ColorChange}) => (
  <div className="Edit-Buttons">
    <div
      tabIndex="0"
      className="Edit-Button"
      style={{ backgroundColor: "#5aac44" }}
      onClick={handleSave}
    >
     {saveLabel}
    </div>

    {handleDelete && (
      <div
        tabIndex="0"
        className="Edit-Button"
        style={{ backgroundColor: "#EA2525", marginLeft: 0 }}
        onClick={handleDelete}
      >
        Delete
      </div>
    )}



    <div tabIndex="0" className="Edit-Button-Cancel" onClick={handleCancel}>
      <ion-icon name="close" />
    </div>
    <button id="colorify">Change color</button>
  </div>



);

export default EditButtons;
