import "../styles/CardEditor.css";
import React, { Component } from "react";
import TextareaAutosize from "react-textarea-autosize";
import EditButtons from "./EditButtons";
import onColor from "./Card"
import { Redirect } from "react-router-dom";
import { props} from './Card';

class CardEditor extends Component {

  state = {
    text: this.props.text || "",
    btn: ""
  };

  handleChangeText = event => this.setState({ text: event.target.value });

  onEnter = e => {
    const { text } = this.state;

    if (e.keyCode === 13) {
      e.preventDefault();
      this.props.onSave(text);
    }
  };
  /*constructor(props) {
    super(props);
}
constructor(props) {
  super(props);
  this.color = this.color.bind(this);
}*/
constructor(props) {
  super(props);
}

//create color function
  color=(Event) => {
    //Event.persist();

    this.setState({btn:Event.target.value});
    this.props.onColor(Event.target.value);
    //this.props.onColor({payload:  Event.target.value})
  };

  render() {
    const { text,btn } = this.state;
    const { onSave, onCancel, onDelete} = this.props;


    return (

      <div className="Edit-Card">
        <div
        className = "Button"
        Button="onClick"
        >
        </div>

        <div className="Card">
          <TextareaAutosize
            autoFocus
            className="Edit-Card-Textarea"
            placeholder="Enter the text for this card..."
            value={text}
            onChange={this.handleChangeText}
            onKeyDown={this.onEnter}
          />

        </div>
        <EditButtons
          handleSave={() => onSave(text)}
          //saveLabel={adding ? "Add card" : "Save"}
          handleDelete={onDelete}
          handleCancel={onCancel}
          handleColor={this.color}
          red={btn}
          //onColor = {this.color}

        />
      </div>

    );
  }
}

export default CardEditor;
