import "../styles/EditButtons.css";
import React from "react";


function changeColor(colorParam) {
  let color = colorParam.value.toLowerCase();
  var optionElement = document.getElementById('rgb');
  optionElement.style.background = color;
};
const EditButtons = ({ handleSave, saveLabel, handleDelete, handleCancel,Redbtn,Bluebtn,Greenbtn}) => (
  <div className="Edit-Buttons">
    <div
      tabIndex="0"
      className="Edit-Button"
      style={{ backgroundColor: "#5aac44" }}
      onClick={handleSave}
    >
     {saveLabel}
    </div>

    {handleDelete && (
      <div
        tabIndex="0"
        className="Edit-Button"
        style={{ backgroundColor: "#EA2525", marginLeft: 0 }}
        onClick={handleDelete}
      >
        Delete
      </div>
    )}

    <div tabIndex="0" className="Edit-Button-Cancel" onClick={handleCancel}>
      <ion-icon name="close" />
    </div>

    <select onchange="changeColor(this);" class="color" id="rgb">
  <option id="red" value="Red">Redbtn</option>
  <option id="green" value="Green">Greenbtn</option>
  <option id="blue" value="Blue">Bluebtn</option>
</select>


  </div>



);

export default EditButtons;
