import "../styles/EditButtons.css";
import React from "react";

const changeColor = document.getElementById('changeColor'),
    colors      = ['red', 'green', 'blue'];
let   colorIndex  = 0;

changeColor.addEventListener('click', () => {
document.body.style.backgroundColor = colors[colorIndex];      
colorIndex = (colorIndex + 1) % colors.length
});

const EditButtons = ({ handleSave, saveLabel, handleDelete, handleCancel,ColorChange}) => (
  <div className="Edit-Buttons">
    <div
      tabIndex="0"
      className="Edit-Button"
      style={{ backgroundColor: "#5aac44" }}
      onClick={handleSave}
    >
     {saveLabel}
    </div>

    {handleDelete && (
      <div
        tabIndex="0"
        className="Edit-Button"
        style={{ backgroundColor: "#EA2525", marginLeft: 0 }}
        onClick={handleDelete}
      >
        Delete
      </div>
    )}

    <div tabIndex="0" className="Edit-Button-Cancel" onClick={handleCancel}>
      <ion-icon name="close" />
    </div>
    <button id="changeColor">changeColor</button>
  </div>



);

export default EditButtons;
